example.code:
public TdmsChannel[] InitializeAnalogWaveformChannelGroup(TdmsChannelGroup group, TdmsDataType waveformDataType, int waveformCount)
{
    // Check if layout requires a timing channel.
    bool includeTimingChannel;    
    switch (group.WaveformLayout)
    {
        case TdmsWaveformLayout.NoTimeChannel:
            includeTimingChannel = false;
            break;
        case TdmsWaveformLayout.PairedTimeAndSampleChannels:
            includeTimingChannel = true;
            break;
        case TdmsWaveformLayout.SingleTimeChannel:
        default:
            throw new InvalidOperationException("AppendAnalogWaveform methods do not support SingleTimeChannel layout mode.");
    }

    // Add timing and data channels for waveforms, as appropriate for the waveform layout.
    TdmsChannelCollection channels = group.GetChannels();
    List<TdmsChannel> dataChannels = new List<TdmsChannel>();
    for (int i = 0; i < waveformCount; ++i)
    {
        if (includeTimingChannel)
            channels.Add(new TdmsChannel("timing" + i, TdmsDataType.DateTime, "Timing channel for waveform " + i));

        TdmsChannel dataChannel = new TdmsChannel("data" + i, waveformDataType, "Data channel for waveform " + i);
        channels.Add(dataChannel);
        dataChannels.Add(dataChannel);
    }
    group.Parent.Save();

    // Return data channels.
    return dataChannels.ToArray();
}

