    amp = RandNumberGenerator.NextDouble() * 10;
    for (int row = 0; row < xData.GetLength(0); row++)
    {
        xData[row, col] = amp * Math.Sin(2 * row * Math.PI / xData.GetLength(0)) + amp;
    }
}

